        key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      } else if (groupBy === 'week') {
        const weekNum = Math.ceil((date.getDate() + new Date(date.getFullYear(), date.getMonth(), 1).getDay()) / 7);
        key = `${date.getFullYear()}-W${String(weekNum).padStart(2, '0')}`;
      } else {
        key = date.toISOString().split('T')[0];
      }

      if (!grouped[key]) {
        grouped[key] = {
          period: key,
          count: 0,
          value: 0,
          deals: []
        };
      }

      grouped[key].count++;
      grouped[key].value += deal.value;
      grouped[key].deals.push({
        id: deal.id,
        title: deal.title,
        value: deal.value,
        assignedTo: deal.assignedTo?.name
      });
    });

    const report = Object.values(grouped);

    // Calcular totais
    const totals = {
      count: deals.length,
      value: deals.reduce((sum, d) => sum + d.value, 0),
      avgValue: deals.length > 0 ? deals.reduce((sum, d) => sum + d.value, 0) / deals.length : 0
    };

    res.json({
      report,
      totals,
      period: {
        start: start.toISOString(),
        end: end.toISOString(),
        groupBy
      }
    });
  } catch (error) {
    logger.error({ error }, 'Error generating sales report');
    res.status(500).json({ error: 'Erro ao gerar relat√≥rio de vendas' });
  }
}

