/**
 * GET /api/reports/conversations
 * Relat√≥rio de conversas
 */
export async function getConversationsReport(req: AuthRequest, res: Response) {
  try {
    const userId = req.user?.id;
    const { startDate, endDate, platform } = req.query;

    const start = startDate ? new Date(startDate as string) : new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
    const end = endDate ? new Date(endDate as string) : new Date();

    const where: any = {
      createdAt: { gte: start, lte: end }
    };

    if (userId && req.user?.role !== 'ADMIN') {
      where.userId = userId;
    }

    if (platform) {
      where.platform = platform;
    }

    // Conversas por status
    const byStatus = await prisma.conversation.groupBy({
      by: ['status'],
      where,
      _count: { id: true }
    });

    // Conversas por plataforma
    const byPlatform = await prisma.conversation.groupBy({
      by: ['platform'],
      where,
      _count: { id: true }
    });

    // Conversas por dia
    const conversations = await prisma.conversation.findMany({
      where,
      select: {
        createdAt: true,
        platform: true,
        status: true
      },
      orderBy: { createdAt: 'asc' }
    });

    const byDay: any = {};
    conversations.forEach(conv => {
      const day = conv.createdAt.toISOString().split('T')[0];
      if (!byDay[day]) {
        byDay[day] = { date: day, count: 0 };
      }
      byDay[day].count++;
    });

    res.json({
      byStatus,
      byPlatform,
      byDay: Object.values(byDay),
      total: conversations.length,
      period: {
        start: start.toISOString(),
        end: end.toISOString()
      }
    });
  } catch (error) {
    logger.error({ error }, 'Error generating conversations report');
    res.status(500).json({ error: 'Erro ao gerar relat√≥rio de conversas' });
  }
}

