/**
 * GET /api/reports/export
 * Exporta relat√≥rio em formato CSV
 */
export async function exportReport(req: AuthRequest, res: Response) {
  try {
    const { type, startDate, endDate } = req.query;

    if (!type) {
      return res.status(400).json({ error: 'Tipo de relat√≥rio √© obrigat√≥rio' });
    }

    const start = startDate ? new Date(startDate as string) : new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
    const end = endDate ? new Date(endDate as string) : new Date();

    let csvData = '';
    let filename = '';

    switch (type) {
      case 'sales':
        const deals = await prisma.deal.findMany({
          where: {
            stage: 'CLOSED_WON',
            closedAt: { gte: start, lte: end }
          },
          include: {
            contact: { select: { name: true } },
            assignedTo: { select: { name: true } }
          }
        });

        csvData = 'ID,T√≠tulo,Valor,Contato,Respons√°vel,Data de Fechamento\n';
        deals.forEach(deal => {
          csvData += `${deal.id},${deal.title},${deal.value},${deal.contact?.name || ''},${deal.assignedTo?.name || ''},${deal.closedAt?.toISOString()}\n`;
        });
        filename = `vendas_${start.toISOString().split('T')[0]}_${end.toISOString().split('T')[0]}.csv`;
        break;

      case 'conversations':
        const conversations = await prisma.conversation.findMany({
          where: {
            createdAt: { gte: start, lte: end }
          },
          include: {
            contact: { select: { name: true } },
            user: { select: { name: true } }
          }
        });

        csvData = 'ID,Contato,Plataforma,Status,Respons√°vel,Data de Cria√ß√£o\n';
        conversations.forEach(conv => {
          csvData += `${conv.id},${conv.contact?.name || ''},${conv.platform},${conv.status},${conv.user?.name || ''},${conv.createdAt.toISOString()}\n`;
        });
        filename = `conversas_${start.toISOString().split('T')[0]}_${end.toISOString().split('T')[0]}.csv`;
        break;

      default:
        return res.status(400).json({ error: 'Tipo de relat√≥rio inv√°lido' });
    }

    res.setHeader('Content-Type', 'text/csv; charset=utf-8');
    res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);
    res.send('\uFEFF' + csvData); // BOM para UTF-8
  } catch (error) {
    logger.error({ error }, 'Error exporting report');
    res.status(500).json({ error: 'Erro ao exportar relat√≥rio' });
  }
}

