import { Prisma } from '@prisma/client';
import { prisma } from '../lib/prisma.js';
import { logger } from '../lib/logger.js';
import { AppError } from '../middleware/error.js';
import { emitToTenant } from '../lib/socket.js';

export const flowsController = {
  async list(req: Request, res: Response) {
    const { page = 1, limit = 20 } = req.query;
    const skip = (Number(page) - 1) * Number(limit);

    const [flows, total] = await Promise.all([
      prisma.flow.findMany({
        where: { tenantId: req.user!.tenantId },
        include: {
          _count: {
            select: { nodes: true, edges: true }
          }
        },
        orderBy: { createdAt: 'desc' },
        skip,
        take: Number(limit)
      }),
      prisma.flow.count({
        where: { tenantId: req.user!.tenantId }
      })
    ]);

    res.json({
      success: true,
      data: flows,
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total,
        totalPages: Math.ceil(total / Number(limit))
      }
    });
  },

  async get(req: Request, res: Response) {
    const { id } = req.params;

    const flow = await prisma.flow.findFirst({
      where: {
        id,
        tenantId: req.user!.tenantId
      },
      include: {
        nodes: {
          orderBy: { createdAt: 'asc' }
        },
        edges: {
          orderBy: { id: 'asc' }
        }
      }
    });

    if (!flow) {
      throw new AppError('Fluxo n√£o encontrado', 404);
    }

    res.json({
      success: true,
      data: flow
    });
  },

  async create(req: Request, res: Response) {
    const { name, description, triggerType, triggerConfig, isActive } = req.body;

    const flow = await prisma.flow.create({
      data: {
        name,
        description,
        tenantId: req.user!.tenantId,
        triggerType: triggerType ?? 'manual',
        triggerConfig: triggerConfig ?? {},
        isActive: isActive ?? true,
        createdBy: req.user!.userId,
        nodes: {
          create: {
            type: 'START',
            label: 'In√≠cio',
            positionX: new Prisma.Decimal(250),
            positionY: new Prisma.Decimal(50),
            config: {}
          }
        }
      },
      include: {
        nodes: true,
        edges: true
      }
    });

    logger.info({ flowId: flow.id, userId: req.user!.userId }, 'Flow created');
    emitToTenant(req.user!.tenantId, 'flow:created', flow);

    res.status(201).json({
      success: true,
      data: flow
    });
  },

  async update(req: Request, res: Response) {
    const { id } = req.params;
    const { name, description, triggerType, triggerConfig, isActive } = req.body;

    const flow = await prisma.flow.update({
      where: {
        id,
        tenantId: req.user!.tenantId
      },
      data: {
        ...(name && { name }),
        ...(description !== undefined && { description }),
        ...(triggerType && { triggerType }),
        ...(triggerConfig && { triggerConfig }),
        ...(isActive !== undefined && { isActive })
      },
      include: {
        nodes: true,
        edges: true
      }
    });

    emitToTenant(req.user!.tenantId, 'flow:updated', flow);

    res.json({
      success: true,
      data: flow
    });
  },

  async delete(req: Request, res: Response) {
    const { id } = req.params;

    await prisma.flow.delete({
      where: {
        id,
        tenantId: req.user!.tenantId
      }
    });

    logger.info({ flowId: id, userId: req.user!.userId }, 'Flow deleted');
    emitToTenant(req.user!.tenantId, 'flow:deleted', { id });

    res.json({
      success: true,
      message: 'Fluxo exclu√≠do'
    });
  },

  async publish(req: Request, res: Response) {
    const { id } = req.params;

    const flow = await prisma.flow.update({
      where: {
        id,
        tenantId: req.user!.tenantId
      },
      data: {
        triggerType: 'published',
        isActive: true,
        version: { increment: 1 }
      },
      include: {
        nodes: true,
        edges: true
      }
    });

    logger.info({ flowId: id, version: flow.version }, 'Flow published');
    emitToTenant(req.user!.tenantId, 'flow:published', flow);

    res.json({
      success: true,
      data: flow
    });
  },

  async duplicate(req: Request, res: Response) {
    const { id } = req.params;

    const original = await prisma.flow.findFirst({
      where: {
