+
+export function useWorkflows() {
+  const queryClient = useQueryClient();
+
+  const listQuery = useQuery({
+    queryKey: ['workflows'],
+    queryFn: () => workflowsService.list(),
+    retry: 1,
+  });
+
+  const publishMutation = useMutation({
+    mutationFn: (id: string) => workflowsService.publishWorkflow(id),
+    onSuccess: (workflow) => {
+      queryClient.setQueryData(['workflows'], (existing: any) => {
+        if (!existing?.data) return existing;
+        return {
+          ...existing,
+          data: existing.data.map((item: Workflow) =>
+            item.id === workflow.id ? workflow : item,
+          ),
+        };
+      });
+    },
+  });
+
+  const duplicateMutation = useMutation({
+    mutationFn: (id: string) => workflowsService.duplicateWorkflow(id),
+    onSuccess: () => {
+      queryClient.invalidateQueries({ queryKey: ['workflows'] });
