import { Request, Response } from 'express';
import { prisma } from '../lib/prisma.js';
import Papa from 'papaparse';
import { z } from 'zod';

// Schema de valida√ß√£o
const contactSchema = z.object({
  name: z.string().min(1, 'Nome √© obrigat√≥rio'),
  phone: z.string().min(10, 'Telefone inv√°lido'),
  email: z.string().email('Email inv√°lido').optional(),
  tags: z.array(z.string()).optional(),
  origem: z.string().optional(),
  leadStatus: z.string().optional(),
});

export const contactsController = {
  /**
   * GET /api/contacts
   * Listar todos os contatos com filtros
   */
  async listContacts(req: Request, res: Response) {
    try {
      const { tenantId } = req.user;
      const { search, tags, origem, leadStatus } = req.query;

      let where: any = { tenantId };

      // Filtro de busca
      if (search) {
        where.OR = [
          { name: { contains: search as string, mode: 'insensitive' } },
          { phone: { contains: search as string } },
          { email: { contains: search as string, mode: 'insensitive' } },
        ];
      }

      // Filtro de tags
      if (tags) {
        const tagsArray = (tags as string).split(',');
        where.tags = { hasSome: tagsArray };
      }

      // Filtro de origem
      if (origem) {
        where.origem = origem;
      }

      // Filtro de status de lead
      if (leadStatus) {
        where.leadStatus = leadStatus;
      }

      const contacts = await prisma.contact.findMany({
        where,
        include: {
          _count: {
            select: { conversations: true },
          },
        },
        orderBy: { createdAt: 'desc' },
      });

      res.json(contacts);
    } catch (error) {
      console.error('Error listing contacts:', error);
      res.status(500).json({ 
        error: 'Erro ao listar contatos',
        message: error.message 
      });
    }
  },

  /**
   * GET /api/contacts/:id
   * Buscar contato por ID
   */
  async getContact(req: Request, res: Response) {
    try {
      const { id } = req.params;
      const { tenantId } = req.user;

      const contact = await prisma.contact.findFirst({
        where: { id, tenantId },
        include: {
          conversations: {
            orderBy: { lastMessageAt: 'desc' },
            take: 5,
          },
          deals: {
            orderBy: { createdAt: 'desc' },
          },
        },
      });

      if (!contact) {
        return res.status(404).json({ error: 'Contato n√£o encontrado' });
      }

      res.json(contact);
    } catch (error) {
      console.error('Error getting contact:', error);
      res.status(500).json({ 
        error: 'Erro ao buscar contato',
        message: error.message 
      });
    }
  },

  /**
   * POST /api/contacts
   * Criar novo contato
   */
  async createContact(req: Request, res: Response) {
    try {
      const { tenantId } = req.user;

      // Validar dados
      const validatedData = contactSchema.parse(req.body);

      // Verificar se j√° existe contato com mesmo telefone
      const existing = await prisma.contact.findFirst({
        where: {
          tenantId,
          phone: validatedData.phone,
        },
      });

      if (existing) {
        return res.status(409).json({ 
          error: 'J√° existe um contato com este telefone' 
        });
      }

      const contact = await prisma.contact.create({
        data: {
          tenantId,
          name: validatedData.name,
          phone: validatedData.phone,
          email: validatedData.email,
          tags: validatedData.tags || [],
          origem: validatedData.origem || 'manual',
          leadStatus: validatedData.leadStatus || 'novo',
        },
      });

      res.status(201).json(contact);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          error: 'Dados inv√°lidos',
          details: error.errors 
        });
      }

      console.error('Error creating contact:', error);
      res.status(500).json({ 
        error: 'Erro ao criar contato',
        message: error.message 
      });
    }
  },

  /**
   * PUT /api/contacts/:id
   * Atualizar contato
   */
  async updateContact(req: Request, res: Response) {
    try {
      const { id } = req.params;
      const { tenantId } = req.user;

      // Verificar se contato existe
      const existing = await prisma.contact.findFirst({
        where: { id, tenantId },
