{"output":"import { Request, Response } from 'express';
import { prisma } from '../lib/prisma.js';
import { logger } from '../lib/logger.js';
import { AuthRequest } from '../middleware/auth.js';

/**
 * GET /api/contacts
 * Lista todos os contatos
 */
export async function getContacts(req: AuthRequest, res: Response) {
  try {
    const userId = req.user?.id;
    const { search, tag, page = '1', limit = '50', sortBy = 'createdAt', order = 'desc' } = req.query;

    const where: any = userId ? { userId } : {};

    if (search) {
      where.OR = [
        { name: { contains: search as string, mode: 'insensitive' } },
        { email: { contains: search as string, mode: 'insensitive' } },
        { phone: { contains: search as string, mode: 'insensitive' } }
      ];
    }

    if (tag) {
      where.tags = {
        has: tag as string
      };
    }

    const skip = (parseInt(page as string) - 1) * parseInt(limit as string);

    const [contacts, total] = await Promise.all([
      prisma.contact.findMany({
        where,
        orderBy: { [sortBy as string]: order },
        skip,
        take: parseInt(limit as string),
        include: {
          _count: {
            select: {
              conversations: true,
              deals: true
            }
          }
        }
      }),
      prisma.contact.count({ where })
    ]);

    res.json({
      contacts,
      pagination: {
        page: parseInt(page as string),
        limit: parseInt(limit as string),
        total,
        pages: Math.ceil(total / parseInt(limit as string))
      }
    });
  } catch (error) {
    logger.error({ error }, 'Error fetching contacts');
    res.status(500).json({ error: 'Erro ao buscar contatos' });
  }
}

