      }
    });

    if (!contact) {
      return res.status(404).json({ error: 'Contato n√£o encontrado' });
    }

    const newTags = contact.tags.filter(tag => !tags.includes(tag));

    const updatedContact = await prisma.contact.update({
      where: { id },
