import { apiClient } from '@/lib/api-client';

export interface ApiResponse<T> {
  data: T;
  message?: string;
  success?: boolean;
}

export interface Pagination {
  page: number;
  limit: number;
  total: number;
  totalPages: number;
}

export interface PaginatedResponse<T> {
  data: T[];
  pagination: Pagination;
}

const wrapResponse = <T>(data: T): ApiResponse<T> => ({
  data,
  success: true,
});

export const api = {
  async get<T>(
    endpoint: string,
    params?: Record<string, any>,
    config?: AxiosRequestConfig,
  ): Promise<ApiResponse<T>> {
    const response = await apiClient.get<T>(endpoint, { params, ...(config ?? {}) });
    return wrapResponse(response.data);
  },

  async post<T>(
    endpoint: string,
    data?: any,
    config?: AxiosRequestConfig,
  ): Promise<ApiResponse<T>> {
    const response = await apiClient.post<T>(endpoint, data, config);
    return wrapResponse(response.data);
  },

  async put<T>(
    endpoint: string,
    data?: any,
    config?: AxiosRequestConfig,
  ): Promise<ApiResponse<T>> {
    const response = await apiClient.put<T>(endpoint, data, config);
    return wrapResponse(response.data);
  },

  async patch<T>(
    endpoint: string,
    data?: any,
    config?: AxiosRequestConfig,
  ): Promise<ApiResponse<T>> {
    const response = await apiClient.patch<T>(endpoint, data, config);
    return wrapResponse(response.data);
  },

  async delete<T>(
    endpoint: string,
    data?: any,
    config?: AxiosRequestConfig,
  ): Promise<ApiResponse<T>> {
    const response = await apiClient.delete<T>(endpoint, { data, ...(config ?? {}) });
    return wrapResponse(response.data);
  },
};
