  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isLoading, setIsLoading] = useState(false);
  const [loginError, setLoginError] = useState('');

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
    if (loginError) setLoginError('');
  };

  const validateForm = () => {
    try {
      loginSchema.parse(formData);
      setErrors({});
      return true;
    } catch (error) {
      if (error instanceof z.ZodError) {
        const newErrors: Record<string, string> = {};
        error.issues.forEach((err) => {
          if (err.path[0]) {
            newErrors[err.path[0] as string] = err.message;
          }
        });
        setErrors(newErrors);
      }
      return false;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsLoading(true);
    setLoading(true);

    try {
      const authResponse = await authService.login({
        email: formData.email,
        password: formData.password,
        rememberMe,
      });

      login(authResponse.user, authResponse.token);
      setLoginError('');

      toast({
        title: 'Login realizado com sucesso!',
        description: `Bem-vindo, ${authResponse.user.name}`,
      });

      navigate('/dashboard');
    } catch (error: any) {
      const message =
        error?.response?.data?.error ??
        error?.message ??
        'E-mail ou senha incorretos. Tente novamente.';
      setLoginError(message);
      toast({
        title: 'Erro ao entrar',
        description: message,
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
      setLoading(false);
    }
  };

  const handleSocialLogin = async (provider: 'google' | 'apple') => {
    setIsLoading(true);
    setLoading(true);

    try {
      const { data, error } = await supabase.auth.signInWithOAuth({
        provider,
        options: {
          redirectTo: `${window.location.origin}/auth/callback`
        }
      });

      if (error) {
        throw error;
      }

      if (data?.url) {
        toast({
          title: 'Redirecionando...',
          description: `Continue o login com ${provider === 'google' ? 'Google' : 'Apple'} na janela segura.`,
        });
        window.location.href = data.url;
      } else {
        toast({
          title: 'Verifique a janela do navegador',
          description: 'Caso o popup não tenha aberto, verifique o bloqueio de pop-ups e tente novamente.',
        });
      }
    } catch (error: any) {
      console.error('Social login error:', error);
      toast({
        title: `Erro ao conectar com ${provider === 'google' ? 'Google' : 'Apple'}`,
        description: error?.message ?? 'Não foi possível realizar o login. Tente novamente.',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-subtle p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        <Card className="shadow-elegant">
          <CardHeader className="text-center space-y-4">
            <motion.div
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.3, delay: 0.1 }}
              className="flex justify-center"
            >
              <img src={logo} alt="PrimeZapAI" className="h-16 w-16" />
            </motion.div>

            <div>
              <CardTitle className="text-2xl font-bold">
                Bem-vindo de volta
              </CardTitle>
              <CardDescription>
                Entre na sua conta PrimeZapAI
              </CardDescription>
            </div>
          </CardHeader>

          <CardContent className="space-y-6">
            {/* Social Login */}
            <div className="space-y-3">
              <Button
                variant="outline"
                className="w-full"
                onClick={() => handleSocialLogin('google')}
                disabled={isLoading}
              >
                <svg className="mr-2 h-4 w-4" viewBox="0 0 24 24">
                  <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
