+
+  # API subdomain: https://api.primezapia.com
+  server {
+    listen 80;
+    server_name api.primezapia.com;
+
+    # Friendly response for the root path (no upstream call)
+    location = / {
+      add_header Content-Type application/json;
+      return 200 '{"status":"ok","service":"primezapia-api"}';
+    }
+
+    # Health-check passthrough
+    location = /health {
+      proxy_pass http://api:3000/health;
+      proxy_set_header Host $host;
+      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
+      proxy_set_header X-Forwarded-Proto $scheme;
+    }
+
+    # Silences browser favicon pings on the API subdomain
+    location = /favicon.ico {
+      return 204;
+    }
+
+    # All other API calls
+    location / {
+      proxy_pass http://api:3000;
+      proxy_set_header Host $host;
+      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
+      proxy_set_header X-Forwarded-Proto $scheme;
+    }
+  }
+
+  # Frontend (primezap.primezapia.com or fallback)
+  server {
+    listen 80;
+    server_name _;
+
+    location = /favicon.ico {
+      return 204;
+    }
+
+    location / {
+      proxy_pass http://web:5173;
+      proxy_set_header Host $host;
+      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
+      proxy_set_header X-Forwarded-Proto $scheme;
+    }
+
+    location /api/ {
