  if (typeof error === 'object' && error !== null) {
    const maybeError = error as {
      response?: { data?: { message?: string; error?: string } };
      message?: string;
    };

    return (
      maybeError.response?.data?.message ??
      maybeError.response?.data?.error ??
      maybeError.message ??
      fallback
    );
  }

  return fallback;
};

